--
-- Déclencheurs `historique_recherches`
--
DELIMITER $$
CREATE TRIGGER `UpdateMotsClesFrequence` AFTER INSERT ON `historique_recherches` FOR EACH ROW BEGIN
    DECLARE v_pos INT DEFAULT 1;
    DECLARE v_word VARCHAR(200);
    DECLARE v_text TEXT;
    SET v_text = NEW.termes_recherche;
    WHILE v_pos > 0 DO
        SET v_pos = LOCATE(' ', v_text);
        IF v_pos > 0 THEN
            SET v_word = TRIM(SUBSTRING(v_text, 1, v_pos - 1));
            SET v_text = SUBSTRING(v_text, v_pos + 1);
        ELSE
            SET v_word = TRIM(v_text);
        END IF;
        IF LENGTH(v_word) > 2 THEN
            VALUES (v_word, 1, 'Recherche')
            ON DUPLICATE KEY UPDATE frequence_utilisation = frequence_utilisation + 1;
        END IF;
    END WHILE;
END
$$
DELIMITER ;

